package data;

import com.fasterxml.jackson.annotation.JsonProperty;

import java.util.List;

/**
 * A record corresponding to a test suite generated by a TOG and its
 * identifying information.
 *
 * @param tog a test oracle generator
 * @param className the fully qualified name of the class under test
 * @param sourceDir the corresponding project source directory
 * @param binDir the corresponding project binaries
 * @param numPass the number of passing tests
 * @param numFail the number of failing tests
 * @param tests a list of all test cases generated by the tog for class
 */
public record TestOutput(
        @JsonProperty("tog") String tog,
        @JsonProperty("className") String className,
        @JsonProperty("sourceDir") String sourceDir,
        @JsonProperty("binDir") String binDir,
        @JsonProperty("numPass") int numPass,
        @JsonProperty("numFail") int numFail,
        @JsonProperty("tests") List<TestCase> tests
) {
}
