package data;

import com.fasterxml.jackson.annotation.JsonIgnoreProperties;

/**
 * This record represents a subset of an OracleDatapoint produced by Tratto.
 * It captures all information needed to create an {@link OracleOutput}.
 *
 * @param oracle the oracle generated by Tratto
 * @param oracleType the type of oracle
 * @param packageName the package name of the class under analysis
 * @param className the simple name of the class under analysis
 * @param methodSourceCode the source code of the method under analysis
 */
@JsonIgnoreProperties(ignoreUnknown = true)
public record TrattoOutput(
        String oracle,
        OracleType oracleType,
        String packageName,
        String className,
        String methodSourceCode
) { }
